<?php 	require_once("koneksi.php");	require("show_message.php");		header('Content-type: application/json');		if(count($_POST) == 0) {		//Kerjakan jika tidak ada data yg masuk				errorMassage();		return;	} else {				$id_barang = $_POST['id_barang'];		$id_user = $_POST['id_user'];				$data_to_update = "";				foreach($_POST as $key => $value) {					$nilai = $value;						//Periksa string, apakah memuat huruf			if( !ctype_digit($value) ) {				$nilai = "'".mysql_escape_string($nilai)."'";			}						if($key == "id_barang" || $key == "id_user") {				continue;			}						$data_to_update = $data_to_update . ", " . $key . "=" . $nilai;			}				$update_query = "UPDATE barang SET " . substr($data_to_update, 1) . " WHERE id_barang = " . $id_barang . " AND id_user = " . $id_user;				$execute_update_query = mysql_query($update_query);				if($execute_update_query) {			successMassage();			return;			} else {			errorMassage();			return;		}		}?>